INSERT INTO `questions` VALUES (16, '大学生易受虚假招聘信息诈骗，下列选项中不属于该项诈骗的是', '以招聘业务员为名发布高薪日结广告', '事主依据广告信息联系被强制要求收取介绍费', '收取正当合理的中介费并安排工作', '要求事主先汇款作为代理商的成本', 'C', 0, '2023-05-12 23:31:27', '2023-05-12 23:31:27', 0);
