INSERT INTO `questions` VALUES (18, '同学乔某准备在网上买一台IPAD，她该如何预防网络购物诈骗', '只顾价格便宜，不问网站真伪', '辨明正规官网，哪怕贵一些，但交易有保障', '用微信、支付宝直接与卖家私下交易，快捷便利', '商家称货到付款，未验收直接付款', 'B', 0, '2023-05-12 23:31:27', '2023-05-12 23:31:27', 0);
INSERT INTO `questions` VALUES (19, '学生熊某想在游戏里充值，此时他该怎么办', '网上找人帮忙低价充值', '官方平台充值', '网上查找便宜充值平台', '找游戏玩家充值', 'B', 0, '2023-05-12 23:31:27', '2023-05-12 23:31:27', 0);
