INSERT INTO `sys_oper_log` VALUES (168, '题目', 2, 'com.ruoyi.plat.controller.QuestionsController.edit()', 'PUT', 1, 'admin', NULL, '/plat/questions', '127.0.0.1', '内网IP', '{\"content\":\"大龄剩男老张为了尽快成家，注册了某婚恋网的会员，成功开启了自己的桃花路。目前正和一位网友聊得火热，女孩时不时给老张发送生活照片并分享自己的经历。照片中的女孩面容姣好，生活中的女孩孝顺乖巧，老张很快坠入了爱河。最近女孩回了老家，并且马上过生日了。女孩想让老张给自己发一个生日红包，并购买一些自己炒的春茶。老张应该怎么做？\",\"correct\":\"DA\",\"createTime\":\"2023-04-25 22:33:43\",\"id\":6,\"isDelete\":0,\"optionA\":\"果断购买，最难消受美人恩，喜欢的人亲手炒的茶一定果断入手。\",\"optionB\":\"我的钱就是她的钱，茶叶不喜欢喝可以不买，红包一定要大大的包。\",\"optionC\":\"提前准备好生日礼物和生日红包，购买茶叶送朋友。\",\"optionD\":\"不发红包也不购买茶叶，换个小号对“女友”进行测试，必要时选择拉黑。\",\"type\":1,\"updateTime\":\"2023-05-05 21:00:26.496\"}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-05-05 21:00:30', 3593);
